import React from 'react';
import { ManagementToolNav, TextField, BasicDropdown } from '../../../common/components';

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function EditItemTemplate(props) {
    const { form, categories, navigateVirtualWarehouse } = props.viewStore;
    return (
        <div>
            <ManagementToolNav />
            <h1>Edit item</h1>
            <Form>
                <Form.Group>
                    <TextField type={'text'} field={form.$('name')} />
                </Form.Group>
                <Form.Group>
                    <BasicDropdown store={categories} type={'text'} field={form.$('categoryId')} />
                </Form.Group>
                <Form.Group>
                    <Form.Text>{form.error}</Form.Text>
                </Form.Group>
                <Form.Group>
                    <Button variant="btn btn-dark" onClick={form.onSubmit}>Edit</Button>
                </Form.Group>
            </Form>
            <Button variant="btn btn-secondary" onClick={navigateVirtualWarehouse}>Back</Button>
        </div>
    )
}

export default EditItemTemplate;
