import React from 'react';
import { inject, observer } from 'mobx-react';
import { AddItemViewStore } from '../stores';
import { AddItemTemplate } from '../../../themes/item/pages';
import Axios from 'axios';

@inject(stores => ({ viewStore: new AddItemViewStore(stores.rootStore) }))
@observer
class AddItem extends React.Component {
    async componentDidMount() {
        debugger
        this.categories = await Axios.get('http://localhost:3001/api/get/categories');
    }
    render() {
        return (
            <AddItemTemplate viewStore={this.props.viewStore} categories={this.categories} />
        )
    }
}

export default AddItem;
