import { observable, action, runInAction, computed } from 'mobx';
import Axios from 'axios';

class AuthStore {
    @observable
    isLoggedIn = false;
    @observable
    loggedInUser = null;

    constructor(rootStore) {
        this.rootStore = rootStore;
    }

    @computed get checkUser() {
        debugger
        return this.isLoggedIn + 1;
    }

    @action.bound
    logIn = async (credentials) => {
        try {
            const users = await Axios.get('http://localhost:3001/api/get/users');
            const user = users.data.filter(u => u.email == credentials.email && u.password == credentials.password);
            if (user.length) {
                runInAction(() => {
                    debugger
                    this.isLoggedIn = true;
                    this.loggedInUser = user[0];
                })
            }
        }
        catch (error) {
            console.log(error);
            throw error;
        }
    }

    @action.bound
    logOut = async () => {
        debugger
        this.isLoggedIn = false;
        this.loggedInUser = null;
    }
}

export default AuthStore;
