import React from 'react';
import { ManagementToolNav } from '../../../common/components'
import { TextField, Dropdown } from '../../../common/components'

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function AddItemTemplate(props) {
    const { form, hasCategories, categories, navigateDashboard } = props.viewStore;
    debugger
    return (
        <div>
            <ManagementToolNav />
            <h1>Add item</h1>
            <Form>
                <Form.Group>
                    <TextField type={'text'} field={form.$('type')} />
                </Form.Group>
                <Form.Group>
                    <TextField type={'text'} field={form.$('categoryId')} />
                </Form.Group>
                <Form.Group>
                    <Button variant="btn btn-dark" onClick={form.onSubmit}>Add</Button>
                </Form.Group>
            </Form>
            <Button variant="btn btn-light" onClick={navigateDashboard}>Back</Button>
        </div>
    )
}

export default AddItemTemplate;
