import { action, observable, runInAction } from "mobx";
import { AddItemForm } from '../forms'
import Axios from "axios";

class AddItemViewStore {
    @observable
    categories;
    constructor(rootStore) {
        this.rootStore = rootStore;
        this.form = new AddItemForm({
            onSuccess: async (form) => {
                const values = form.values();
                try {
                    debugger
                    const user = await Axios.post('http://localhost:3001/api/create/item', { name: values.name, categoryId: parseInt(values.categoryId) });
                } catch (error) {
                    debugger
                }
            },
            onError: (form) => {
                debugger
            }
        })
    }

    @action.bound
    getCategories = async () => {
        const categories = await Axios.get('http://localhost:3001/api/get/categories');
        runInAction(() => {
            this.categories = categories;
        })
    }

    navigateDashboard = () => {
        this.rootStore.routerStore.goTo('dashboard');
    }
}

export default AddItemViewStore;
