import { observable, action, computed } from 'mobx';

class VirtualWarehouseViewStore {
    @observable
    person;
    @observable
    categories;

    @computed
    get isOwner() {
        if (this.person.hasOwnProperty('name')) {
            return true;
        }
        return false;
    }

    constructor(rootStore) {
        this.rootStore = rootStore;
        this.person = JSON.parse(sessionStorage.getItem('person'));
    }

    @action.bound
    getCategories = async () => {
        const categories = await Axios.get('http://localhost:3001/api/get/categories');
        console.log('Success', categories);
        runInAction(() => {
            this.categories = categories;
        })
    }

    navigateDashboard = () => {
        this.rootStore.routerStore.goTo('dashboard');
    }
}

export default VirtualWarehouseViewStore;
