import React from 'react';

import TableContainer from '@material-ui/core/TableContainer';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import TableBody from '@material-ui/core/TableBody';
import Paper from '@material-ui/core/Paper';
import Icon from '@material-ui/core/Icon';

import Button from 'react-bootstrap/Button';

function BasicTable(props) {
    const { items, isOwner, actions } = props;
    return (
        <React.Fragment>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell align="center">Name</TableCell>
                            <TableCell align="center">Category</TableCell>
                            <TableCell align="center">Edit</TableCell>
                            <TableCell align="center">Delete</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {items.data.map(i => (
                            <TableRow key={i.id}>
                                <TableCell component="th" scope="row">{i.name}</TableCell>
                                <TableCell align="center">{i.type}</TableCell>
                                <TableCell align="center"><Button variant="btn btn-info" onClick={e => actions.navigateEditItem(i.id)}><Icon fontSize='small'>edit</Icon></Button></TableCell>
                                <TableCell align="center"><Button variant="btn btn-danger" onClick={e => actions.deleteItem(i.id)}><Icon fontSize='small'>delete</Icon></Button></TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </React.Fragment>
    )
}

export default BasicTable;
