import React from 'react';
import { ManagementToolNav } from '../../../common/components'

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function LoginTemplate(props) {
    const { navigateRegister } = props.viewStore;
    return (
        <div>
            <ManagementToolNav />
            <h1>Login</h1>
            <Form>
                <Form.Group>
                    <MembershipInput type={'text'} field={this.props.viewStore.form.$('email')} />
                </Form.Group>
                <Form.Group>
                    <MembershipInput type={'password'} field={this.props.viewStore.form.$('password')} />
                </Form.Group>
                <Form.Group>
                    <Form.Text>{this.props.viewStore.form.error}</Form.Text>
                </Form.Group>
                <Form.Group>
                    <Button type='submit' onClick={this.props.viewStore.form.onSubmit} variant='danger'>Log in</Button>
                </Form.Group>
            </Form>
            <Button className="btn btn-dark" onClick={navigateRegister}>Go to Register</Button>
        </div>
    )
}

export default LoginTemplate;
